import React from 'react';
import './App.css';
import axios from 'axios'
import { Button } from '@material-ui/core'

class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: "",
            show: false
        }
    }

    handleShow = (e) => {
        console.log(e.currentTarget.id);
        this.setState({
            show: true
        })
    }

    handleHide = (e) => {
        console.log(e.currentTarget.id);
        this.setState({
            show: false
        })
        const things = {
            id: 4,
            content: "Ann"
        }
        axios({
            method: 'post',
            url: 'http://localhost:8080/greeting',
            data: {
                things
            }
        })
    }

    componentDidMount() {
        axios.get('http://localhost:8080/greeting')
            .then((response) => {
                console.log(response)
                this.setState({
                    data: response.data.content
                })
            });
    }

    render() {
        return (
            <div>
                <Button id="1" align="left" variant="contained" onClick={this.handleShow}>Show</Button>
                <Button id="2" align="right" variant="contained" onClick={this.handleHide}>Hide</Button>
                {this.state.show && <h1>{this.state.data}</h1>}
            </div>
        );
    }
}

export default Test;
