import React from 'react';
import { Modal, IconButton } from '@material-ui/core';
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';

class AcademicMode extends React.Component {
    constructor(props){
        super(props)
        let open = this.props.academicMode;
        this.state = {
            isModalOpen: open
        }
    }

    handleClose = () => {
        this.setState({
            isModalOpen: false
        })
    }

    render() {
        return (
            <div>
                <Modal id="menu" open={this.state.isModalOpen} onClose={this.handleClose}>
                    <div style={{ top: 450, left: 25}} className="paper">
                        <div>
                            <h2>Text in a modal</h2>
                            <IconButton style={{ position: 'absolute', right: 2, top: 20 }}>
                                <ArrowForwardIcon/>
                            </IconButton>
                        </div>
                        <p>Duis mollis, est non commodo luctus, nisi erat porttitor ligula.</p>
                    </div>
                </Modal>
            </div>
        )
    }
}

export default AcademicMode;
