import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import queryString from 'query-string';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import "../css/Level15.css";

const initialValues = {
    password: '',
    confirmPassword: ''
};

const validationSchema = Yup.object().shape({
    password: Yup.string()
        .min(6, 'Password must be at least 6 characters')
        .required('Password is required'),
    confirmPassword: Yup.string()
        .oneOf([Yup.ref('password'), null], 'Passwords must match')
        .required('Confirm Password is required'),
});

class ResetPassword extends React.Component {

    onSubmit = ({ password, confirmPassword }) => {
        console.log(password, confirmPassword);
        if (password === confirmPassword) {
            console.log('cool')
        }
            // alertService.clear();
            // accountService.resetPassword({ token, password, confirmPassword })
            //     .then(() => {
            //         alertService.success('Password reset successful, you can now login', { keepAfterRouteChange: true });
            //         history.push('login');
            //     })
            //     .catch(error => {
            //         setSubmitting(false);
            //         alertService.error(error);
            //     });
    }

    render() {
        return (
            <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={this.onSubmit}>
                {({errors, touched}) => (
                    <Form>
                        <br/>
                        <h2 style={{ textAlign: 'center' }}>Reset Password</h2>
                        <div className="card-body">
                            <div className="form-group">
                                <label>Password</label>
                                <Field name="password" type="password"
                                       className={'form-control' + (errors.password && touched.password ? ' is-invalid' : '')}/>
                                <ErrorMessage name="password" component="div" className="invalid-feedback"/>
                            </div>
                            <div className="form-group">
                                <label>Confirm Password</label>
                                <Field name="confirmPassword" type="password"
                                       className={'form-control' + (errors.confirmPassword && touched.confirmPassword ? ' is-invalid' : '')}/>
                                <ErrorMessage name="confirmPassword" component="div" className="invalid-feedback"/>
                            </div>
                            <div className="form-row">
                                <div className="form-group col">
                                    <button type="submit"  className="signin-btn">
                                        Reset Password
                                    </button>
                                    <a href='javascript:void(0)' onClick={() => this.props.changeView('resetToLogin')}>Cancel</a>
                                </div>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        );
    }
}

export default ResetPassword;
