import React from 'react';
import NavBar from '../Initial/NavBar';
import Messages from '../Initial/Messages';

class Home extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            isPhoneClicked: false,
            academicMode: true,
            messages : [
                {
                    text: "Welcome Agent. You are now a part of an elite academy of spies dedicated to understanding computers. " +
                        "You will be tasked with different missions to find jewels. Collecting jewels will advance you through the " +
                        "ranks to becoming a top agent. ",
                    member: {
                        color: "black",
                        username: "Mission Control"
                    }
                },
                {
                    text: "Your mission, should you choose to accept it, is to find the jewel on the next page. " +
                        "Click the check mark to begin. ",
                    member: {
                        color: "black",
                        username: "Mission Control"
                    }
                }
            ],
            member: {
                username: 'spy',
                color: 'blue'
            }
        };
    }

    getPhoneClick = () => {
        this.setState({
            isPhoneClicked: true
        })
    }

    getModeSwitch = () => {
        this.setState(prevSate => ({
            academicMode: !prevState.academicMode
        }))
    }

    render() {
        return (
            <div>
                {this.state.isPhoneClicked && <Messages
                    messages={this.state.messages}
                    currentMember={this.state.member}
                />}
                <NavBar getPhoneClick={this.getPhoneClick} clicked={this.state.isPhoneClicked} academicMode={this.state.academicMode} getModeSwitch={this.getModeSwitch}/>
            </div>
        );
    }
}

export default Home;
