import React from 'react';
import { Modal } from '@material-ui/core';

class AcademicMode extends React.Component {
    constructor(props){
        super(props)
        let open = this.props.academicMode;
        this.state({
            isModalOpen: open
        })
    }

    handleClose = () => {
        this.setState({
            isModalOpen: false
        })
    }

    render() {
        return (
            <div>
                <Modal open={this.state.isModalOpen} onClose={this.handleClose}>
                    <div>
                        <h2>Text in a modal</h2>
                        <p>Duis mollis, est non commodo luctus, nisi erat porttitor ligula.</p>
                    </div>
                </Modal>
            </div>
        )
    }
}

export default AcademicMode;
