import React from 'react';
import '../css/Level5.css';
import Level from '../util/Level';
import MuiAlert from '@material-ui/lab/Alert';
import Snackbar from '@material-ui/core/Snackbar';

import { App } from '../util/PasswordReset/app';
import { configureFakeBackend } from "../util/PasswordReset/_helpers";
import "../css/SignIn.css";
import { accountService} from "../util/PasswordReset/_services";


import Login from "../util/Login";
import axios from "axios";

class Level15 extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            isJewelFound: false,
            academicMode: this.props.academicMode,
        }
    }

    handleFound = () => {
        this.setState({
            isJewelFound: true,
        })
    }

    handleCloseDialog = () => {
        this.setState({
            isJewelFound: false
        })
        accountService.logout();
        localStorage.clear();
        this.props.getLevelChange()
    }

    checkLogin = (email, password) => {
        console.log(email, password)
        axios.get('http://localhost:5050/user')
            .then((response) => {
                console.log(response.data);
                if (email === response.data.email && password === response.data.password) {
                    console.log("woohoo")
                }
                else {
                    console.log("sad")
                }
            });
    }

    handleClose = () => {
        console.log("close")
    }

    render() {
        configureFakeBackend();
        return (
            <Level
                academicmode={this.props.academicmode}
                getModeSwitch={this.props.getModeSwitch}
                mission=''
                openHint={this.props.openHint}
                closeHint={this.props.closeHint}
                isHintShown={this.props.isHintShown}
                hintMessage=''
                isJewelFound={this.state.isJewelFound}
                handleCloseDialog={this.handleCloseDialog}
                dialogMessage='Your mission is complete! <TODO> Click the X to return to the home page and receive your next mission.'
            >
                <div>
                    <Snackbar open={true} autoHideDuration={6000} anchorOrigin={{ vertical: 'top', horizontal: 'right' }} style={{ top: '100px' }} onClose={this.handleClose}>
                        <MuiAlert elevation={6} variant="filled" severity="error">This in an error message</MuiAlert>
                    </Snackbar>
                    <br/>
                    <Login checkLogin={this.checkLogin}/>
                </div>
            </Level>
        );
    }
}

export default Level15;
