import React from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import "../css/Level15.css";
import MuiAlert from "@material-ui/lab/Alert";
import Snackbar from "@material-ui/core/Snackbar";

const initialValues = {
    email: ''
};

const validationSchema = Yup.object().shape({
    email: Yup.string()
        .email('Email is invalid')
        .required('Email is required')
});

class ForgotPassword extends React.Component {
    constructor() {
        super();
        this.state = {
            success: false,
        }
    }

    handleClose = () => {
        this.setState({
            success: false
        })
    }

    onSubmit = ({ email }) => {
        console.log(email)

        this.setState({
            success: true,
        })

        // alertService.clear();
        // accountService.forgotPassword(email)
        //     .then(() => alertService.success('Please check your email for password reset instructions'))
        //     .catch(error => alertService.error(error))
        //     .finally(() => setSubmitting(false));
    }

    render() {
        return (
            <div>
                <Snackbar open={this.state.success} autoHideDuration={5000}
                          anchorOrigin={{vertical: 'top', horizontal: 'right'}} style={{top: '100px'}}
                          onClose={this.handleClose}>
                    <MuiAlert elevation={6} variant="filled" severity="success">Success</MuiAlert>
                </Snackbar>
                <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={this.onSubmit}>
                    {({errors, touched}) => (
                        <Form>
                            <br/>
                            <h2 style={{textAlign: 'center'}}>Forgot Password</h2>
                            <div className="card-body">
                                <div className="form-group">
                                    <label>Email</label>
                                    <Field name="email" type="text"
                                           className={'form-control' + (errors.email && touched.email ? ' is-invalid' : '')}/>
                                    <ErrorMessage name="email" component="div" className="invalid-feedback"/>
                                </div>
                                <div>
                                    <div>
                                        <button type="submit" className="signin-btn">
                                            Submit
                                        </button>
                                    </div>
                                    <div>
                                        <a href='javascript:void(0)' onClick={this.props.changeView}>Cancel</a>
                                    </div>
                                </div>

                            </div>
                        </Form>
                    )}
                </Formik>
            </div>
        )
    }
}

export default ForgotPassword;
